import React, { Component } from 'react'; 
import Footer from './footer';
import ReactTable from 'react-table';
import "react-table/react-table.css";
import "../App.css";

class Stocktable extends Component {
    //state initialization
  state = {
    data: [],
    id: 0,
    message: null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null
  };

  //when component first mounts, it fetchs existing data in db
  componentDidMount() {
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({
        intervalIsSet: interval
      });
    }
  }
//kill process
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({
        intervalIsSet: null
      });
    }
  }

  //fetch data from database
  getDataFromDb = () => {
    fetch("/api/stocks")
      .then(data => data.json())
      .then(res=> this.setState({ data: res.data}));
  };

  //fetch 10 high performing stocks from database
  

  render() {
    const { data } = this.state;
    const columns = [{
              columns: [{
                  Header: "Symbol",
                  accessor: "symbol"
                }]
            },
            {
              Header: "Stock Info",
              columns: [{
                  Header: "Open",
                  accessor: "open"
                },
                {
                  Header: "Date",
                  accessor: "date"
                },
                {
                  Header: "Close",
                  accessor: "close"
                },
                {
                  Header: "Low",
                  accessor: "low"
                },
                {
                  Header: "High",
                  accessor: "high"
                },
                {
                  Header: "Volume",
                  accessor: "volume"
                }
              ]
            }
          ];
    return (
        <div>
     <div className="reacTable">
        <ReactTable
        data = {data}
        columns={columns}
        defaultPageSize = {10}
        defaultSorted = {
          [{
            id: "high",
            desc: true
          }]
        }
        
        className = "-striped -highlight"
        
        SubComponent={row => {
            return (
              <div style={{ padding: "20px" }}>
                <em>
                  StockInfo
                </em>
                <br />
                <br />
                <ReactTable
                  data={data}
                  columns={columns}
                  defaultPageSize={3}
                  showPagination={false}
                  SubComponent={row => {
                    return (
                      <div style={{ padding: "20px" }}>
                        Another Sub Component!
                      </div>
                    );
                  }}
                />
              </div>
            );
          }}
        />
     </div>
     <Footer />
     </div>
    );
  }
}

export default Stocktable;
