import React, { Component } from 'react';
import axios from "axios";
import BootstrapTable from 'react-bootstrap-table-next';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';

class App extends Component {
  //state initialization
  state = {
    data: [],
    id: 0,
    message: null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null
  };

  //when component first mounts, it fetchs existing data in db
  componentDidMount() {
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({
        intervalIsSet: interval
      });
    }
  }
//kill process
  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({
        intervalIsSet: null
      });
    }
  }

  //fetch data from database
  getDataFromDb = () => {
    fetch("/api/stocks")
      .then(data => data.json())
      .then(res=> this.setState({ data: res.data}));
  };



 //fetch default high performing stocks from database
  render() {
    const { data } = this.state;
     const columns = [{
       dataField: 'date',
       text: 'Date',
       sort: true
     }, {
       dataField: 'symbol',
       text: 'Symbol'
     }, {
       dataField: 'open',
       text: 'Open'
     }, {
       dataField: 'close',
       text: 'Close'
     }, {
       dataField: 'low',
       text: 'Low'
     }, {
       dataField: 'high',
       text: 'High',
       sort: true
     }, {
       dataField: 'volume',
       text: 'Volume',
       sort: true
     }];

     const defaultSorted = [{
       dataField: 'high',
       order: 'desc'
     }];
     const CaptionElement = () => < h3 style = {
       {
         borderRadius: '0.25em',
         textAlign: 'center',
         color: 'purple',
         border: '1px solid purple',
         padding: '0.5em'
       }
     } > Component as Header < /h3>;

    return (

     <div>
    <BootstrapTable
      bootstrap4
      keyField="id"
      data={ data }
      columns={ columns }
      defaultSorted={ defaultSorted } 
      caption = { < CaptionElement / >
      }
      striped
      hover
      condensed
    />

     </div>
    );
  }
}

export default App;
